class Title extends Component {
  constructor () {
    super()
    this.state = {
      isHidden: true
    }
    this.props.text = "Cyberpunk RPG Character Sheet"
  }
  //
  // toggleHidden () {
  //   this.setState({
  //     isHidden: !this.state.isHidden
  //   });
  // }

  render() {
    return (
      <h1>this.props.text</h1>
    );
  }
}

export default Title;
