import Form from 'react-bootstrap/Form'
import '../styles/StatsForm.css';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button';
import '../styles/StatGrid.css';
import '../styles/NameField.css';
import '../styles/SaveButton.css';

class StatsForm extends Component {
  constructor(props) {
    super(props)
    // this.state = {
    //   character: [
    //     {
    //       name: 'default',
    //       str: 10,
    //       dex: 10,
    //       int: 10,
    //       skills: ['living']
    //     }
    //   ]
    // }
    this.state = { character: [], }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    fetch('/default-char')
      .then(res => res.json())
      .then(character => this.setState({ character }));
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
      // alert('A character was submitted: ' + this.state.value);
      alert('Character:' + this.state.character);
      event.preventDefault();
  }

  render() {
    return (

      <div id='parent'>
      <div id='full-form'>
          this.state.character.map((value) =>
            <span>{value.name}</span>
            <span>{value.str}</span>
            <span>{value.dex}</span>
            <span>{value.int}</span>
            <span>{value.skills}</span>
          )
      </div>
      <div><SaveButton/></div>
      </div>

    );
  }
}
