import React, { Component } from 'react';
// import NameField from './NameField'
// import SaveButton from './SaveButton'
// import StatGrid from './StatGrid'
import Form from 'react-bootstrap/Form'
import '../styles/StatsForm.css';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button';
import '../styles/StatGrid.css';
import '../styles/NameField.css';
import '../styles/SaveButton.css';

class StatsForm extends Component {
  constructor(props) {
    super(props)
    // this.state = {
    //   character: [
    //     {
    //       name: 'default',
    //       str: 10,
    //       dex: 10,
    //       int: 10,
    //       skills: ['living']
    //     }
    //   ]
    // }
    this.state = { character: [] }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    fetch('/character')
      .then(res => res.json())
      .then(character => this.setState({ character }))
      .then ((responseJson) => {
        console.log('response Steve:' + responseJson);})
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
      // alert('A character was submitted: ' + this.state.value);
      alert('Character:' + this.state.character);
      event.preventDefault();
  }

  render() {
    return (
      <div id='full-form'>
          <h1>Character:</h1>
      </div>
    );
  }
}

export default StatsForm;
