import React, { Component } from 'react';
// import NameField from './NameField'
// import SaveButton from './SaveButton'
// import StatGrid from './StatGrid'
import Form from 'react-bootstrap/Form'
import '../styles/StatsForm.css';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button';
import '../styles/StatGrid.css';
import '../styles/NameField.css';
import '../styles/SaveButton.css';

class StatsForm extends Component {
  constructor(props) {
    super(props)
    this.state = {character: ''};
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
      // alert('A character was submitted: ' + this.state.value);
      var character = {
        name: this.refs.name.value,
        str: 10,
        dex: 10,
        int: 10,
        skills: ['living'],

        // methods
        buildJSON: function() {
          return JSON.stringify(this);
        }
      };
      alert('Character:' + character.buildJSON());
      event.preventDefault();
  }

  render() {
    return (
      <Form onSubmit={this.handleSubmit} id='full-form'>
        <label id='name-field' ref="name">
          Name:
          <input type="text" id="char-name"/>
        </label>
        <Table bordered size="sm" id="stat-table">
          <thead>
            <tr>
              <th>Stat</th>
              <th>Base</th>
              <th>+</th>
              <th>Mods</th>
              <th>=</th>
              <th>Value</th>
            </tr>
          </thead>
          <tbody>
            <tr id='strength'>
              <td>STR</td>
              <td><input type="number" id="str-base"/></td>
              <td>+</td>
              <td><input type="number" id="str-mod"/></td>
              <td>=</td>
              <td><input type="number" readOnly id="str-value"/></td>
            </tr>
            <tr id='dexterity'>
              <td>DEX</td>
              <td><input type="number" id="dex-base"/></td>
              <td>+</td>
              <td><input type="number" id="dex-mod"/></td>
              <td>=</td>
              <td><input type="number" readOnly id="dex-value"/></td>
            </tr>
            <tr id='intelligence'>
              <td>INT</td>
              <td><input type="number" id="int-base"/></td>
              <td>+</td>
              <td><input type="number" id="int-mod"/></td>
              <td>=</td>
              <td><input type="number" readOnly id="int-value"/></td>
            </tr>
          </tbody>
        </Table>
        <Button variant="primary" type="submit" id="save">Save</Button>
      </Form>
    );
  }
}
// <div><NameField/></div>
// <div><StatGrid/></div>
// <div><SaveButton/></div>
export default StatsForm;
