import React, { Component } from 'react';
import Table from 'react-bootstrap/Table'

export default class StatGrid extends Component {
  render() {
    return (
      <Table bordered>
        <thead>
          <tr>
          <th>Stat</th>
          <th>Base</th>
          <th>+</th>
          <th>Mods</th>
          <th>=</th>
          <th>Value</th>
          </tr>
        /></thead>
      </Table>
    );
  }
}
